'From nsboot-2011-08-03 of 3 August 2011 [latest update: #10966] on 7 August 2012 at 11:21:14 am'!Object subclass: #XMLNode	instanceVariableNames: 'visitMarker '	classVariableNames: 'CanonicalTable '	poolDictionaries: ''	category: 'XML-Parser'!!XMLNode methodsFor: 'as yet unclassified' stamp: 'yk 3/2/2012 16:33'!markVisit: marker	"Use to mark node visitations, marker is any value the visitor uses for	tracking"	visitMarker:= marker! !!XMLNode methodsFor: 'as yet unclassified' stamp: 'yk 3/2/2012 16:33'!visitMarker	^ visitMarker! !!XMLTokenizer methodsFor: 'tokenizing' stamp: 'yk 6/13/2012 14:48'!nextTag	| tagName attributes nextChar namespaces |	(self peek = $/)		ifTrue: [^self nextEndTag].	tagName := self nextName.	self skipSeparators.	attributes := Dictionary new: 33.	"Add location info for error reporting and such (YK)"	attributes at: #XMLTagStartPosition put: stream position.	namespaces := Dictionary new: 5.	[(nextChar := self peek) == $> or: [nextChar == $/]] whileFalse: [		self checkAndExpandReference: #content.		self nextAttributeInto: attributes namespaces: namespaces.		self skipSeparators.].	self handleStartTag: tagName attributes: attributes namespaces: namespaces.	self next == $/		ifTrue: [			self handleEndTag: tagName.			self next].	! !!XMLTokenizer methodsFor: 'errors' stamp: 'yk 6/15/2012 15:12'!parseError: errorString	SAXParseException signal: errorString, ' at position ', stream position asString! !!XMLWriter methodsFor: 'writing xml' stamp: 'yk 6/27/2012 09:07'!attribute: attributeName value: attributeValue	(attributeName = #XMLTagStartPosition) 		ifTrue: [^self].	self stream		space;		nextPutAll: attributeName.	self		eq;		putAsXMLString: attributeValue! !Object subclass: #XMLNode	instanceVariableNames: 'visitMarker'	classVariableNames: 'CanonicalTable'	poolDictionaries: ''	category: 'XML-Parser'!